package Com;

public class Demo1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=20;
		System.out.println("The value of a and b are a="+a+" b="+b);
		int help;
		help=a;
		a=b;
		b=help;
		System.out.println("After we swap we get the value of a and b are "+a+""  +b);
		

	}

}
